package w1d1;

public class Character {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String Name="Saravanan";
		int  age = 25;
		long  creditCardNum    = 18391831938l;
		float  mileage         = 20.5f;
		double  bankBalance = 6549.82983;
		boolean  hadLunch = true;
		char  logo     = 'H';
		String colour="RED";
		String  brandName  =  "Audi";
		
		System.out.println("Name : "+Name);
		System.out.println("My age is : "+age);
		System.out.println("The credit card number is : "+creditCardNum +" The mileage is : " +mileage);
		System.out.println("The logo of my car is : "+logo);
		System.out.println("Bank balance is "+bankBalance);
		System.out.println("The brand name is :"+brandName);
		System.out.println(hadLunch);
		System.out.println("Vehicle color: "+ colour);
		
	}

}
