package w1d1;

public class Number {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
    int a=5,b=9,c;
    c=a+b;
    System.out.println("The output value is: "+c);
    
    if (c/2==0) {
    	System.out.println("given number is even: "+c);
		
	} else {
		System.out.println("Given number is odd: "+c);
	}
	}
	
	

}
